CREATE TABLE program_info (id int primary key auto_increment,program_type enum("电影", "电视剧", "动漫", "综艺"),name varchar(255),poster_url varchar(800),edition varchar(255),fragment_number int default 1,fragment_order int default 1,director_name varchar(255),main_performer varchar(800),program_classification varchar(255),program_area varchar(255),program_language varchar(255),release_year int,time_length int,play_number int default 0,play_number_in_today int default 0,program_introduce varchar(800),program_score double,m3u8_link varchar(800));