CREATE TABLE program_info (id int primary key auto_increment,program_type enum("电影", "电视剧", "动漫", "综艺"),name varchar(255),poster_url varchar(800),edition varchar(255),director_name varchar(255),main_performer varchar(800),program_classification varchar(255),program_area varchar(255),program_language varchar(255),release_year int,time_length int,play_number int default 0,play_number_in_today int default 0,program_introduce varchar(800),program_score double,m3u8_link varchar(800));CREATE TABLE website_info (id int primary key auto_increment,visit_total int,visit_number_in_today int,search_number int);CREATE TABLE user_list (id int primary key auto_increment,cookie varchar(255));CREATE TABLE watch_notes (id int primary key auto_increment,program_id int,user_cookie varchar(255),watch_time_length int);